const watchStyles = "./assets/scss/**/*.scss";

// Path to all vendor JS files.
const watchJsVendor = "./assets/js/vendor/*.js";

// Path to all custom JS files.
const watchJsCustom = "./assets/js/custom/*.js";

// Path to all PHP files.
const watchPhp = "./**/*.php";

// >>>>> Zip file config.
// Must have.zip at the end.
const zipName = "terraria-shop.zip";

// Must be a folder outside of the zip folder.
const zipDestination = "./../"; // Default: Parent folder.
const zipIncludeGlob = ["./**/*"]; // Default: Include all files/folders in current directory.

// Default ignored files and folders for the zip file.
const zipIgnoreGlob = ["!./{node_modules,node_modules/**/*}", "!./.git", "!./.svn", "!./gulpfile.babel.js", "!./wpgulp.config.js", "!./.eslintrc.js", "!./.eslintignore", "!./.editorconfig", "!./phpcs.xml.dist", "!./vscode", "!./package.json", "!./package-lock.json", "!./assets/scss/**/*", "!./assets/scss", "!./assets/img/raw/**/*", "!./assets/img/raw", `!${imgSRC}`, `!${styleSRC}`, `!${jsCustomSRC}`, `!${jsVendorSRC}`];

// >>>>> Translation options.
// Your text domain here.
const textDomain = "terraria-shop";

// Name of the translation file.
const translationFile = "terraria-shop.pot";

// Where to save the translation files.
const translationDestination = "./languages";

// Package name.
const packageName = "terraria-shop";

// Where can users report bugs.
const bugReport = "";

// Last translator Email ID.
const lastTranslator = "";

// Team's Email ID.
const team = "";

// Browsers you care about for auto-prefixing. Browserlist https://github.com/ai/browserslist
// The following list is set as per WordPress requirements. Though; Feel free to change.
const BROWSERS_LIST = ["last 2 version", "> 1%"];

// Export.
module.exports = {
  projectURL,
  productURL,
  browserAutoOpen,
  injectChanges,
  styleSRC,
  styleDestination,
  outputStyle,
  errLogToConsole,
  precision,
  jsVendorSRC,
  jsVendorDestination,
  jsVendorFile,
  jsCustomSRC,
  jsCustomDestination,
  jsCustomFile,
  imgSRC,
  imgDST,
  watchStyles,
  watchJsVendor,
  watchJsCustom,
  watchPhp,
  zipName,
  zipDestination,
  zipIncludeGlob,
  zipIgnoreGlob,
  textDomain,
  translationFile,
  translationDestination,
  packageName,
  bugReport,
  lastTranslator,
  team,
  BROWSERS_LIST
};
